"""
{
  "title": "Action/Animation Utilities",
  "category": "animation",
  "tags": ["action", "animation", "nla", "keyframe", "clip"],
  "description": "Functions for managing animation actions and NLA.",
  "blender_version": "3.0+"
}
"""
import bpy


def create_action(name: str) -> bpy.types.Action:
    """Create a new action."""
    return bpy.data.actions.new(name)


def assign_action(
    obj: bpy.types.Object,
    action: bpy.types.Action
) -> None:
    """Assign action to object."""
    if not obj.animation_data:
        obj.animation_data_create()
    obj.animation_data.action = action


def duplicate_action(
    action: bpy.types.Action,
    new_name: str = None
) -> bpy.types.Action:
    """Duplicate an action."""
    new_action = action.copy()
    if new_name:
        new_action.name = new_name
    return new_action


def push_action_to_nla(
    obj: bpy.types.Object,
    action: bpy.types.Action = None,
    track_name: str = "NLATrack"
) -> bpy.types.NlaStrip:
    """Push action to NLA track."""
    if not obj.animation_data:
        obj.animation_data_create()

    action = action or obj.animation_data.action
    if not action:
        return None

    track = obj.animation_data.nla_tracks.new()
    track.name = track_name

    strip = track.strips.new(action.name, int(action.frame_range[0]), action)

    return strip


def create_nla_strip(
    obj: bpy.types.Object,
    action: bpy.types.Action,
    start_frame: int,
    track_name: str = "Track"
) -> bpy.types.NlaStrip:
    """Create NLA strip from action."""
    if not obj.animation_data:
        obj.animation_data_create()

    track = None
    for t in obj.animation_data.nla_tracks:
        if t.name == track_name:
            track = t
            break

    if not track:
        track = obj.animation_data.nla_tracks.new()
        track.name = track_name

    strip = track.strips.new(action.name, start_frame, action)
    return strip


def set_strip_blend_mode(
    strip: bpy.types.NlaStrip,
    blend_type: str = 'REPLACE'
) -> None:
    """Set NLA strip blend mode."""
    strip.blend_type = blend_type  # REPLACE, ADD, SUBTRACT, MULTIPLY


def set_strip_extrapolation(
    strip: bpy.types.NlaStrip,
    extrapolation: str = 'HOLD'
) -> None:
    """Set NLA strip extrapolation."""
    strip.extrapolation = extrapolation  # NOTHING, HOLD, HOLD_FORWARD


def scale_action(
    action: bpy.types.Action,
    scale: float
) -> None:
    """Scale action keyframes."""
    for fcurve in action.fcurves:
        for keyframe in fcurve.keyframe_points:
            keyframe.co.x *= scale
            keyframe.handle_left.x *= scale
            keyframe.handle_right.x *= scale


def offset_action(
    action: bpy.types.Action,
    offset: float
) -> None:
    """Offset action keyframes."""
    for fcurve in action.fcurves:
        for keyframe in fcurve.keyframe_points:
            keyframe.co.x += offset
            keyframe.handle_left.x += offset
            keyframe.handle_right.x += offset


def list_actions() -> list:
    """Return list of all action names."""
    return [a.name for a in bpy.data.actions]


def delete_action(name: str) -> None:
    """Delete action by name."""
    if name in bpy.data.actions:
        bpy.data.actions.remove(bpy.data.actions[name])


def clear_animation(obj: bpy.types.Object) -> None:
    """Clear all animation from object."""
    if obj.animation_data:
        obj.animation_data.action = None
        for track in obj.animation_data.nla_tracks:
            obj.animation_data.nla_tracks.remove(track)

"""
{
  "title": "Scene Cleanup Utilities",
  "category": "clean",
  "tags": ["delete", "remove", "clean", "purge"],
  "description": "Functions to clear the scene or remove unused data blocks."
}
"""
import bpy

def delete_all_objects():
    if bpy.context.active_object and bpy.context.active_object.mode == 'EDIT':
        bpy.ops.object.mode_set(mode='OBJECT')

    bpy.ops.object.select_all(action='SELECT')
    bpy.ops.object.delete(use_global=False)

def delete_object(obj):
    bpy.data.objects.remove(obj, do_unlink=True)

def purge_orphans():
    # Purge unused data blocks (meshes, materials, textures, etc.)
    for block in bpy.data.meshes:
        if block.users == 0:
            bpy.data.meshes.remove(block)

    for block in bpy.data.materials:
        if block.users == 0:
            bpy.data.materials.remove(block)

    for block in bpy.data.textures:
        if block.users == 0:
            bpy.data.textures.remove(block)

    for block in bpy.data.images:
        if block.users == 0:
            bpy.data.images.remove(block)

"""
{
  "title": "Collection Management Utilities",
  "category": "organization",
  "tags": ["collection", "organize", "layer", "visibility", "hierarchy"],
  "description": "Functions for organizing objects into collections.",
  "blender_version": "3.0+"
}
"""
import bpy


def create_collection(
    name: str,
    parent: bpy.types.Collection = None
) -> bpy.types.Collection:
    """
    Create a new collection.

    Args:
        name: Collection name
        parent: Parent collection (uses scene collection if None)

    Returns:
        The created collection
    """
    collection = bpy.data.collections.new(name)

    if parent:
        parent.children.link(collection)
    else:
        bpy.context.scene.collection.children.link(collection)

    return collection


def move_to_collection(
    obj: bpy.types.Object,
    collection: bpy.types.Collection,
    unlink_others: bool = True
) -> None:
    """Move object to collection."""
    if unlink_others:
        for coll in obj.users_collection:
            coll.objects.unlink(obj)

    if obj.name not in collection.objects:
        collection.objects.link(obj)


def move_selected_to_collection(
    collection: bpy.types.Collection,
    unlink_others: bool = True
) -> None:
    """Move all selected objects to collection."""
    for obj in bpy.context.selected_objects:
        move_to_collection(obj, collection, unlink_others)


def get_or_create_collection(name: str) -> bpy.types.Collection:
    """Get existing or create new collection."""
    if name in bpy.data.collections:
        return bpy.data.collections[name]
    return create_collection(name)


def set_collection_visibility(
    collection: bpy.types.Collection,
    visible: bool = True,
    viewport: bool = True,
    render: bool = True
) -> None:
    """Set collection visibility."""
    # Find layer collection
    def find_layer_collection(layer_coll, name):
        if layer_coll.name == name:
            return layer_coll
        for child in layer_coll.children:
            result = find_layer_collection(child, name)
            if result:
                return result
        return None

    layer_coll = find_layer_collection(
        bpy.context.view_layer.layer_collection,
        collection.name
    )

    if layer_coll:
        layer_coll.exclude = not visible

    collection.hide_viewport = not viewport
    collection.hide_render = not render


def delete_collection(
    collection: bpy.types.Collection,
    delete_objects: bool = False
) -> None:
    """Delete collection."""
    if delete_objects:
        for obj in collection.objects[:]:
            bpy.data.objects.remove(obj)

    bpy.data.collections.remove(collection)


def organize_by_type() -> dict:
    """Organize scene objects by type into collections."""
    collections = {}

    type_names = {
        'MESH': 'Meshes',
        'CURVE': 'Curves',
        'LIGHT': 'Lights',
        'CAMERA': 'Cameras',
        'ARMATURE': 'Armatures',
        'EMPTY': 'Empties'
    }

    for obj in bpy.context.scene.objects:
        type_name = type_names.get(obj.type, 'Other')

        if type_name not in collections:
            collections[type_name] = get_or_create_collection(type_name)

        move_to_collection(obj, collections[type_name])

    return collections


def select_collection_objects(collection: bpy.types.Collection) -> None:
    """Select all objects in collection."""
    bpy.ops.object.select_all(action='DESELECT')
    for obj in collection.objects:
        obj.select_set(True)


def get_collection_bounds(collection: bpy.types.Collection) -> tuple:
    """Get bounding box of all objects in collection."""
    min_co = [float('inf')] * 3
    max_co = [float('-inf')] * 3

    for obj in collection.objects:
        if obj.type == 'MESH':
            for corner in obj.bound_box:
                world_co = obj.matrix_world @ corner
                for i in range(3):
                    min_co[i] = min(min_co[i], world_co[i])
                    max_co[i] = max(max_co[i], world_co[i])

    return tuple(min_co), tuple(max_co)

"""
{
  "title": "Object Selection Utilities",
  "category": "selection",
  "tags": ["select", "deselect", "active", "find"],
  "description": "Helper functions to manage object selection state in the scene."
}
"""
import bpy

def select_all():
    bpy.ops.object.select_all(action='SELECT')

def deselect_all():
    bpy.ops.object.select_all(action='DESELECT')

def select_object(obj):
    obj.select_set(True)
    bpy.context.view_layer.objects.active = obj

def select_by_name(name):
    deselect_all()
    obj = bpy.data.objects.get(name)
    if obj:
        select_object(obj)
        return obj
    return None

def select_by_type(type='MESH'):
    deselect_all()
    for obj in bpy.context.scene.objects:
        if obj.type == type:
            obj.select_set(True)

def invert_selection():
    bpy.ops.object.select_all(action='INVERT')

"""
{
  "title": "Viewport Utilities",
  "category": "interface",
  "tags": ["viewport", "3d", "view", "camera", "navigation"],
  "description": "Functions for controlling 3D viewport settings.",
  "blender_version": "3.0+"
}
"""
import bpy


def frame_selected() -> None:
    """Frame selected objects in viewport."""
    bpy.ops.view3d.view_selected()


def frame_all() -> None:
    """Frame all objects in viewport."""
    bpy.ops.view3d.view_all()


def set_view(view: str) -> None:
    """
    Set viewport to preset view.

    Args:
        view: 'FRONT', 'BACK', 'LEFT', 'RIGHT', 'TOP', 'BOTTOM', 'CAMERA'
    """
    views = {
        'FRONT': 'FRONT',
        'BACK': 'BACK',
        'LEFT': 'LEFT',
        'RIGHT': 'RIGHT',
        'TOP': 'TOP',
        'BOTTOM': 'BOTTOM',
        'CAMERA': 'CAMERA'
    }

    if view in views:
        bpy.ops.view3d.view_axis(type=views[view])


def toggle_orthographic() -> None:
    """Toggle orthographic/perspective view."""
    bpy.ops.view3d.view_persportho()


def set_shading_mode(mode: str) -> None:
    """
    Set viewport shading mode.

    Args:
        mode: 'WIREFRAME', 'SOLID', 'MATERIAL', 'RENDERED'
    """
    for area in bpy.context.screen.areas:
        if area.type == 'VIEW_3D':
            for space in area.spaces:
                if space.type == 'VIEW_3D':
                    space.shading.type = mode


def set_solid_shading_options(
    color_type: str = 'MATERIAL',
    light: str = 'STUDIO',
    show_shadows: bool = True
) -> None:
    """Configure solid shading."""
    for area in bpy.context.screen.areas:
        if area.type == 'VIEW_3D':
            for space in area.spaces:
                if space.type == 'VIEW_3D':
                    shading = space.shading
                    shading.color_type = color_type
                    shading.light = light
                    shading.show_shadows = show_shadows


def show_overlays(show: bool) -> None:
    """Toggle viewport overlays."""
    for area in bpy.context.screen.areas:
        if area.type == 'VIEW_3D':
            for space in area.spaces:
                if space.type == 'VIEW_3D':
                    space.overlay.show_overlays = show


def show_grid(show: bool) -> None:
    """Toggle floor grid visibility."""
    for area in bpy.context.screen.areas:
        if area.type == 'VIEW_3D':
            for space in area.spaces:
                if space.type == 'VIEW_3D':
                    space.overlay.show_floor = show


def show_axes(show: bool) -> None:
    """Toggle axes visibility."""
    for area in bpy.context.screen.areas:
        if area.type == 'VIEW_3D':
            for space in area.spaces:
                if space.type == 'VIEW_3D':
                    space.overlay.show_axis_x = show
                    space.overlay.show_axis_y = show
                    space.overlay.show_axis_z = show


def set_clip_distances(near: float, far: float) -> None:
    """Set viewport clip start/end."""
    for area in bpy.context.screen.areas:
        if area.type == 'VIEW_3D':
            for space in area.spaces:
                if space.type == 'VIEW_3D':
                    space.clip_start = near
                    space.clip_end = far


def toggle_xray() -> None:
    """Toggle X-Ray mode."""
    bpy.context.space_data.shading.show_xray = not bpy.context.space_data.shading.show_xray


def set_focal_length(length: float) -> None:
    """Set viewport camera focal length."""
    for area in bpy.context.screen.areas:
        if area.type == 'VIEW_3D':
            for space in area.spaces:
                if space.type == 'VIEW_3D':
                    space.lens = length
